INSERT INTO event VALUES (190, 906, 'Knights of the Dinner Table(tm) Live Reading', 'Knights of the Dinner Table(tm) Live Reading', NULL, NULL, NULL, NULL, 1, 'Anybody who has played role-playing games will enjoy this live reading of the funniest comic book in the genre.  Unpaid (but funny) Ben Con Staff and Volunteers will take on the roles of B.A., Bob, Dave, Sara, and Brian as they try and muddle through another session of HackMaster.   Maybe you\'d like to jump in as one of the characters. Give it a try! Guaranteed to be a laugh!', '0', 'Beginner', 'Special Event', NULL, NULL, NULL, NULL, 1, 20020714202833);
