INSERT INTO event VALUES (192, 908, 'Introduction to AD&D', 'Introduction to AD&D', NULL, NULL, NULL, NULL, 1, 'Come learn to play the most popular Role-Playing game in the world in a non-threatening setting.  These beginner events will be run by some of the most famous, or infamous, Role-Playing judges in the Mountain States region.', '3', 'Beginner-Rules Taught', 'Demonstration', NULL, '', NULL, NULL, 1, 20020714203052);
