INSERT INTO event VALUES (14, 100, 'Unlimited Boardgame & Puffing Billy Pass', 'Unlimited Boardgame & Puffing Billy Pass', NULL, NULL, NULL, NULL, 0, 'This pass will allow you to play in any  Board Game or Puffing Billy Event at Ben Con.  Simply show up at the time listed for the event in the Board Game Room, and you will be grouped into tables.  ALWAYS BRING A COPY OF THE GAMES YOU WANT TO PLAY!', '5', 'Beginner-Rules taught', 'Boardgame', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (15, 101, 'Rio Grande Games Demo', 'Rio Grande Games Demo', NULL, 'Jay Tummelson', NULL, NULL, 1, 'Come join Jay Tummelson of Rio Grande Games as he demos their latest games. Jay will be in the board game room running demos throughout the convention, so stop by and see what is happening at Rio Grande Games.', '0', 'Beginner-Rules Taught', 'Demonstration', NULL, NULL, 'Rio Grande Games', NULL, 1, 20020714202833);
INSERT INTO event VALUES (16, 102, 'Cheap Ass Games Demo', 'Cheap Ass Games Demo', NULL, NULL, NULL, NULL, 1, 'The kooks from Cheap Ass games will be demonstrating their cheap and crazy games all weekend long. Come drop by their space in the dealer\'s room and try out one of their many wacky games.', '0', 'Beginner-Rules Taught', 'Demonstration', NULL, NULL, 'Cheap Ass Games', NULL, 1, 20020714202833);
INSERT INTO event VALUES (17, 110, 'Acquire', 'Acquire', NULL, NULL, NULL, NULL, 0, 'Join the ranks of the rich and famous in Avalon Hill\'s game of hotel empires and mergers.', '2', 'Beginner-Rules Taught', 'Boardgame', NULL, NULL, 'Avalon Hill', NULL, 1, 20020714202833);
INSERT INTO event VALUES (18, 111, 'Advanced Civilization', 'Advanced Civilization', NULL, NULL, NULL, NULL, 0, 'Can you raise your people out of the stone age and lead the world into civilization? Come and try in this Avalon Hill game, but be careful or else disaster will strike at the most inopportune time.', '2', 'Intermediate', 'Boardgame', NULL, NULL, 'Avalon Hill', NULL, 1, 20020714202833);
INSERT INTO event VALUES (19, 112, 'Africa 1880', 'Africa 1880', NULL, NULL, NULL, NULL, 0, 'Relive the colonial adventure with Africa 1880, as each nation colonizes Africa and races for domination of the continent. A game of intrigue and diplomacy.', '2', 'Beginner-Rules taught', 'Boardgame', NULL, NULL, 'Clash of Arms', NULL, 1, 20020714202833);
INSERT INTO event VALUES (20, 113, 'Age of Renaissance', 'Age of Renaissance', NULL, NULL, NULL, NULL, 0, 'Become the dominant European power by establishing market dominance around the globe in Avalon Hill\'s latest game that traces the rise of western civilization.  This event is for advanced players of the game.', '2', 'Advanced', 'Boardgame', NULL, NULL, 'Avalon Hill', NULL, 1, 20020714202833);
INSERT INTO event VALUES (21, 114, 'Age of Renaissance', 'Age of Renaissance', NULL, NULL, NULL, NULL, 0, 'Become the dominant European power by establishing market dominance around the globe in Avalon Hill\'s latest game that traces the rise of western civilization. This session is designed to introduce those who have never played Age of Renaissance  to this great game. ', '2', 'Beginner-Rules taught', 'Boardgame', NULL, NULL, 'Avalon Hill', NULL, 1, 20020714202833);
INSERT INTO event VALUES (22, 115, 'Axis & Allies: Head-to-Head', 'Axis & Allies', 'Head-to-Head', NULL, NULL, NULL, 0, 'A single elimination tournament of Milton Bradley\'s classic game of World War II. Each player will play either the Axis or Allies. Winners keep advancing until only one is left. ', '2', 'Intermediate', 'Boardgame', NULL, NULL, 'Milton Bradley', NULL, 1, 20020714202833);
INSERT INTO event VALUES (23, 116, 'Axis & Allies: Multi-Player', 'Axis & Allies', 'Multi-Player', NULL, NULL, NULL, 0, 'Blitz your way through the frozen steppes of Russia and wade through the Pacific in Milton Bradley\'s game of World War II.', '2', 'Intermediate', 'Boardgame', NULL, NULL, 'Milton Bradley', NULL, 1, 20020714202833);
INSERT INTO event VALUES (24, 117, 'B-17', 'B-17', NULL, 'Mike Moore', NULL, NULL, 0, 'Come be part of this Rocky Mountain cult classic, as you lead your bombers to their selected target and try to make it home. In one piece that is. ', '2', 'Beginner', 'Boardgame', NULL, NULL, 'Avalon Hill', NULL, 1, 20020714202833);
INSERT INTO event VALUES (25, 118, 'Battlemist', 'Battlemist', NULL, NULL, NULL, NULL, 1, 'Lead the forces of the Elves, the Dwarves, or possibly even the Dark One himself in this fantasy board game of conquest and hero-questing. We will be using the recently introduced expansion, Sails of War, to add seafaring to game.', '2', 'Beginner', 'Boardgame', NULL, NULL, 'Fantasy Flight Games', NULL, 1, 20020714202833);
INSERT INTO event VALUES (26, 119, 'Britannia', 'Britannia', NULL, NULL, NULL, NULL, 0, 'Join the Romans, the Anglos, the Saxons, the Norwegians, the Danes, and the Normans as they all invade Britain in Avalon Hill\'s historical game of early British history.', '2', 'Intermediate', 'Boardgame', NULL, NULL, 'Avalon Hill', NULL, 1, 20020714202833);
INSERT INTO event VALUES (27, 120, 'Colonial Diplomacy', 'Colonial Diplomacy', NULL, NULL, NULL, NULL, 0, 'A new twist on the classic game of Diplomacy. This time the back-stabbing takes place in colonial times and locales.', '2', 'Advanced', 'Boardgame', NULL, NULL, 'Avalon Hill', NULL, 1, 20020714202833);
INSERT INTO event VALUES (28, 121, 'Condottieri', 'Condottieri', NULL, NULL, NULL, NULL, 0, 'The Italian city-states are fighting for domination and survival in this classic European import from Eurogames. A simple game anybody can learn, but playing it is a challenge.', '2', 'Beginner-Rules taught', 'Boardgame', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (29, 122, 'Conquest of the Empire', 'Conquest of the Empire', NULL, NULL, NULL, NULL, 0, 'Milton Bradley\'s classic game of the Roman Empire\'s civil wars. Can you unify the republic and become emperor of Rome?', '2', 'Beginner', 'Boardgame', NULL, NULL, 'Milton Bradley', NULL, 1, 20020714202833);
INSERT INTO event VALUES (30, 123, 'Cosmic Encounter', 'Cosmic Encounter', NULL, NULL, NULL, NULL, 0, 'Almost anything can happen in Mayfair\'s game of intergalactic colonization that breaks its own rules.', '2', 'Beginner', 'Boardgame', NULL, NULL, 'Mayfair Games', NULL, 1, 20020714202833);
INSERT INTO event VALUES (31, 124, 'Detroit Grand Prix', 'Detroit Grand Prix', NULL, NULL, NULL, NULL, 0, 'Zoom around the track and see who can finish first in Mayfair\'s game of Grand Prix racing in the US.', '2', 'Beginner-Rules Taught', 'Boardgame', NULL, NULL, 'Mayfair Games', NULL, 1, 20020714202833);
INSERT INTO event VALUES (32, 125, 'Diplomacy', 'Diplomacy', NULL, NULL, NULL, NULL, 0, 'The classic Avalon Hill game of negotiating and back-stabbing politics, set in pre-WWI Europe.', '2', 'Advanced', 'Boardgame', NULL, NULL, 'Avalon Hill', NULL, 1, 20020714202833);
INSERT INTO event VALUES (33, 126, 'El Grande', 'El Grande', NULL, NULL, NULL, NULL, 0, 'Ah, the glory of old Spain. Can you and your caballeros become the power behind the throne? Find out in this classic European import.', '2', 'Beginner-Rules taught', 'Boardgame', NULL, NULL, 'Rio Grande Games', NULL, 1, 20020714202833);
INSERT INTO event VALUES (34, 127, 'Formula Dé', 'Formula Dé', NULL, NULL, NULL, NULL, 0, 'Join the European Formula One circuit and see who can survive the race through all the twisty corners.', '2', 'Beginner', 'Boardgame', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (35, 128, 'Fortress America', 'Fortress America', NULL, NULL, NULL, NULL, 0, 'They are attacking the US from all sides in Milton Bradley\'s futuristic game of an invasion of the US.', '2', 'Intermediate', 'Boardgame', NULL, NULL, 'Milton Bradley', NULL, 1, 20020714202833);
INSERT INTO event VALUES (36, 129, 'Hannibal', 'Hannibal', NULL, NULL, NULL, NULL, 0, 'Avalon Hill\'s game covering the 2nd Punic War, Carthage vs Rome. So pack up those elephants and head over the Alps to invade Italy. Based upon the highly acclaimed \'We The People\' system.  This is a single elimination event, with the winners going on to play the other winners.', '2', 'Advanced', 'Boardgame', NULL, NULL, 'Avalon Hill', NULL, 1, 20020714202833);
INSERT INTO event VALUES (37, 130, 'History of the World', 'History of the World', NULL, NULL, NULL, NULL, 0, 'Replay the past in this Avalon Hill game of global conquest. A great game for beginners and experts alike.', '2', 'Intermediate', 'Boardgame', NULL, NULL, 'Avalon Hill', NULL, 1, 20020714202833);
INSERT INTO event VALUES (38, 131, 'Joan of Arc', 'Joan of Arc', NULL, NULL, NULL, NULL, 1, 'The era is the 100 Years War and France is in turmoil. Can you be the one to lead the forces to unite France? Come try this exciting new game. ', '2', 'Beginner-Rules Taught', 'Boardgame', NULL, NULL, 'Clash of Arms', NULL, 1, 20020714202833);
INSERT INTO event VALUES (39, 132, 'Junta', 'Junta', NULL, NULL, NULL, NULL, 0, 'As part of the ruling junta, you scrape the barrel for all the pork you can find - until the revolution comes, that is. West End\'s exciting game about politics in the Banana Republic.', '2', 'Intermediate', 'Boardgame', NULL, NULL, 'West End Games', NULL, 1, 20020714202833);
INSERT INTO event VALUES (40, 133, 'Kingmaker', 'Kingmaker', NULL, NULL, NULL, NULL, 0, 'Who is the legitimate heir to the throne of England? You will decide in Avalon Hill\'s game that recreates the War of the Roses.', '2', 'Advanced', 'Boardgame', NULL, NULL, 'Avalon Hill', NULL, 1, 20020714202833);
INSERT INTO event VALUES (41, 134, 'Kings & Things', 'Kings & Things', NULL, NULL, NULL, NULL, 0, 'Recruit your rag-tag army from the various things that are out there in your quest to be King over all in this exciting West End game. But beware the Big JuJu!', '2', 'Beginner', 'Boardgame', NULL, NULL, 'West End Games', NULL, 1, 20020714202833);
INSERT INTO event VALUES (42, 135, 'Machiavelli', 'Machiavelli', NULL, NULL, NULL, NULL, 0, 'Can you expand your influence and bring the entire Italian peninsula under your control? Or will a smiling opponent defeat your carefully laid plans with a treacherous  stab in the back?', '2', 'Advanced', 'Boardgame', NULL, NULL, 'Avalon Hill', NULL, 1, 20020714202833);
INSERT INTO event VALUES (43, 136, 'Medici', 'Medici', NULL, NULL, NULL, NULL, 1, 'The market is open and the bidding starts. Gather up the goods you\'ve purchased on your boat as you sail to foreign markets to sell your wares. Remember, bid low, sell high', '2', 'Beginner-Rules Taught', 'Boardgame', NULL, NULL, 'Rio Grande Games', NULL, 1, 20020714202833);
INSERT INTO event VALUES (44, 137, 'Merchant of Venus', 'Merchant of Venus', NULL, NULL, NULL, NULL, 0, 'Is that an alien artifact in your pants or is it your designer genes showing off again? Come play Avalon Hill\'s game of intergalatic exploration and trading.', '2', 'Beginner', 'Boardgame', NULL, NULL, 'Avalon Hill', NULL, 1, 20020714202833);
INSERT INTO event VALUES (45, 138, 'Modern Art', 'Modern Art', NULL, NULL, NULL, NULL, 0, 'How much do I hear for this fine piece of art? $10,000, $20,000, ... $50,000? Come bid on the artwork and see if you can determine which works are masterpieces, and which aren\'t worth the canvas they\'re painted on.', '2', 'Beginner-Rules taught', 'Boardgame', NULL, NULL, 'Mayfair Games', NULL, 1, 20020714202833);
INSERT INTO event VALUES (46, 139, 'Monopoly', 'Monopoly', NULL, NULL, NULL, NULL, 0, 'Parker Brothers\' game of capitalism in Atlantic City.  Buy properties, build hotels, collect rent and see if you can force the other players into bankruptcy. ', '2', 'Beginner-Rules Taught', 'Boardgame', NULL, NULL, 'Parker Brothers', NULL, 1, 20020714202833);
INSERT INTO event VALUES (47, 140, 'Nuclear War', 'Nuclear War', NULL, NULL, NULL, NULL, 0, 'Come play in this fast and loose game, where the body count is always in the millions (or is that billions). But beware of the Supervirus, it could strike anywhere, anytime.', '2', 'Beginner-Rules Taught', 'Boardgame', NULL, NULL, 'Flying Buffalo', NULL, 1, 20020714202833);
INSERT INTO event VALUES (48, 141, 'Risk', 'Risk', NULL, NULL, NULL, NULL, 0, 'Conquer the world in Parker Brothers\' game of global domination. ', '2', 'Beginner', 'Boardgame', NULL, NULL, 'Parker Brothers', NULL, 1, 20020714202833);
INSERT INTO event VALUES (49, 142, 'Robo Rally', 'Robo Rally', NULL, 'Kevin Scarlett', NULL, NULL, 0, 'All right, who left the robots out on the factory floor? Again! Come join in the race as you play demented robots running around the factory in an insane race.', '2', 'Beginner-Rules Taught', 'Boardgame', NULL, NULL, 'WotC', NULL, 1, 20020714202833);
INSERT INTO event VALUES (50, 143, 'Samurai', 'Samurai', NULL, NULL, NULL, NULL, 0, 'Position your forces to gain control over the important resources of Japan in the newest game from award-winning designer Reiner Knizia.', '2', 'Beginner-Rules taught', 'Boardgame', NULL, NULL, 'Rio Grande Games', NULL, 1, 20020714202833);
INSERT INTO event VALUES (51, 144, 'Samurai Swords', 'Samurai Swords', NULL, NULL, NULL, NULL, 0, 'As a great Daimyo, can you bring all of Japan under your control and win the title of Shogun, or will you face defeat and disgrace and be forced to commit seppuku? Find out in Milton Bradley\'s game of Japanese warfare. Formerly published as Shogun.', '2', 'Intermediate', 'Boardgame', NULL, NULL, 'Milton Bradley', NULL, 1, 20020714202833);
INSERT INTO event VALUES (52, 145, 'Serenissima', 'Serenissima', NULL, NULL, NULL, NULL, 0, 'Load up your galleys with gold, spice, and gems and sail the Mediterranean in EuroGames game of renaissance trading and conflict.', '2', 'Beginner', 'Boardgame', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (53, 146, 'Settlers of Catan', 'Settlers of Catan', NULL, NULL, NULL, NULL, 0, 'Join in the land rush and play in Mayfair\'s wildly popular game of colonization and building. So simple, just about anybody can learn this game in 5 minutes.', '2', 'Beginner', 'Boardgame', NULL, NULL, 'Mayfair', NULL, 1, 20020714202833);
INSERT INTO event VALUES (54, 147, 'Successors', 'Successors', NULL, NULL, NULL, NULL, 0, 'Alexander the Great is dead. Can you pick up the pieces of his empire and lead the Macedonian empire to greatness? Try this new Avalon Hill multi-player game based upon the award winning system used in \'We The People\' and Hannibal.', '2', 'Intermediate', 'Boardgame', NULL, NULL, 'Avalon Hill', NULL, 1, 20020714202833);
INSERT INTO event VALUES (55, 148, 'Talisman', 'Talisman', NULL, NULL, NULL, NULL, 0, 'Fight your way to the top and command all that you behold in Games Workshop\'s magical quest game.', '2', 'Beginner', 'Boardgame', NULL, NULL, 'Games Workshop', NULL, 1, 20020714202833);
INSERT INTO event VALUES (56, 149, 'The Hobbit', 'The Hobbit', NULL, NULL, NULL, NULL, 0, 'You\'re a hobbit out for a bit of adventure and fun, but beware of the unexpected in this Iron Crown boardgame. Great for kids of all ages.', '2', 'Beginner-Rules taught', 'Boardgame', NULL, NULL, 'I.C.E.', NULL, 1, 20020714202833);
INSERT INTO event VALUES (57, 150, 'Tigris & Eurphrates', 'Tigris & Eurphrates', NULL, NULL, NULL, NULL, 0, 'The dawn of civilization. Can you lead your people out of the quagmire and forge them into the dominant kingdom? Come and try in Hans im Glück\'s newest and most challenging game.', '2', 'Beginner-Rules taught', 'Boardgame', NULL, NULL, 'Mayfair', NULL, 1, 20020714202833);
INSERT INTO event VALUES (58, 151, 'Titan', 'Titan', NULL, NULL, NULL, NULL, 1, 'This is truly a classic monster game. No, not that type of monster game. Gather up all sorts of  monsters and go beat up on the others until there is only one Titan left standing.', '2', 'Intermediate', 'Boardgame', NULL, NULL, 'Avalon Hill', NULL, 1, 20020714202833);
INSERT INTO event VALUES (59, 152, 'Titan - The Arena', 'Titan - The Arena', NULL, NULL, NULL, NULL, 1, 'It\'s a struggle to the finish to see which monsters will be left alive at the end of the day. Did you bet on the right monsters? Come play this enjoyable, simple game, inspired by the classic monster game \'Titan\'.', '2', 'Beginner-Rules Taught', 'Boardgame', NULL, NULL, 'Avalon Hill', NULL, 1, 20020714202833);
INSERT INTO event VALUES (60, 153, 'Twilight Imperium', 'Twilight Imperium', NULL, NULL, NULL, NULL, 0, 'Join the galactic  struggle as the great races prepare themselves to become the progenitors of a new galatic empire. ', '2', 'Intermediate', 'Boardgame', NULL, NULL, 'Fantasy Flight', NULL, 1, 20020714202833);
INSERT INTO event VALUES (61, 154, 'We the People', 'We the People', NULL, NULL, NULL, NULL, 0, 'Send the British packing or crush the rebellion in Avalon Hill\'s game of the American Revolution. This is a single elimination tournament.', '2', 'Advanced', 'Boardgame', NULL, NULL, 'Avalon Hill', NULL, 1, 20020714202833);
INSERT INTO event VALUES (62, 155, 'Republic of Rome', 'Republic of Rome', NULL, 'Steve Weeldreyer', NULL, NULL, 1, 'Can you gather up your faction and become the first man of Rome, or will you be fiddling while Rome burns to the ground. Come and find out in Avalon Hill\'s exciting simulation of the Roman Republic', '2', 'Intermediate', 'Boardgame', NULL, NULL, 'Avalon Hill', NULL, 1, 20020714202833);
INSERT INTO event VALUES (63, 156, 'Lost Worlds: Weekend Duel', 'Lost Worlds', 'Weekend Duel', 'Ed Davis', NULL, NULL, 1, 'This is an ongoing event. Take your ticket to the Digital Dungeon booth in the dealer\'s room. Ed will sign you up and give you a button with a picture of your book\'s character on it. Challenge any other player you see wearing a Lost Worlds button. The player with the most winds without losing three lives is declared victor. Wins are registered at the Digital Dungeon booth during dealer\'s hours. Players can not be challenged while playing another event. Presented by Digital Dungeon.  All weekend until 2PM Monday.', '2', 'Beginner', 'Boardgame', NULL, NULL, 'Flying Buffalo', NULL, 1, 20020714202833);
INSERT INTO event VALUES (64, 201, 'Rail Baron', 'Rail Baron', NULL, NULL, NULL, NULL, 0, 'Race from destination to destination while amassing railroad deeds and cash in this classic train game from Avalon Hill. Presented by the Train Gamers Association.', '2', 'Beginner', 'Puffing Billy', NULL, NULL, 'Avalon Hill', NULL, 1, 20020714202833);
INSERT INTO event VALUES (65, 202, 'Express', 'Express', NULL, NULL, NULL, NULL, 1, 'A fast-paced card game of train formation from Mayfair Games.', '2', 'Beginner', 'Puffing Billy', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (66, 203, 'Eurorails', 'Eurorails', NULL, NULL, NULL, NULL, 1, NULL, '2', 'Intermediate', 'Puffing Billy', NULL, '', NULL, NULL, 1, 20020714203041);
INSERT INTO event VALUES (67, 204, '1830', '1830', NULL, NULL, NULL, NULL, 0, 'Play this Avalon Hill game of railroad and market manipulation.', '2', 'Advanced', 'Puffing Billy', NULL, NULL, 'Avalon Hill', NULL, 1, 20020714202833);
INSERT INTO event VALUES (68, 205, 'Empire Builder / North American Rails', 'Empire Builder / North American Rails', NULL, NULL, NULL, NULL, 0, 'Construct your own transcontinental rail empire to win this Mayfair Games\' event with a one-track mind. Slots 1 & 4 are slated for Empire Builder, while Slots 2 & 5 are slated for North American Rails.', '2', 'Intermediate', NULL, NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (69, 206, 'Railway Rivals', 'Railway Rivals', NULL, NULL, NULL, NULL, 0, 'A fast and furious game by Games Workshop of building railways and racing between cities over the track. Players choose the board.', '2', 'Beginner', 'Puffing Billy', NULL, NULL, 'Games Workshop', NULL, 1, 20020714202833);
INSERT INTO event VALUES (70, 207, 'Iron Dragon', 'Iron Dragon', NULL, NULL, NULL, NULL, 0, 'Try this fantasy variant of the Empire Builder system by Mayfair Games set in the magical realm of Darwinia.', '2', 'Intermediate', 'Puffing Billy', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (71, 208, 'Freight Train', 'Freight Train', NULL, NULL, NULL, NULL, 0, 'Here\'s a quick and easy card game of trains in the freight yard by Alan Moon. Published by Mayfair Games, Inc.', '2', 'Beginner', 'Puffing Billy', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (72, 209, 'Empire Builder International Open', 'Empire Builder International Open', NULL, NULL, NULL, NULL, 1, 'We will be offering a variety of train game variants based on the Empire Builder system by Mayfair Games.  Choose and play.  If there’s enough time, you may be able to play more than one.  Variants offered:  Eurorails, Australian Rails, British Rails, Nippon Rails, and Iron Dragon.', '2', 'Intermediate', 'Puffing Billy', NULL, NULL, 'Mayfair Games', NULL, 1, 20020714202833);
INSERT INTO event VALUES (73, 210, 'Santa Fe', 'Santa Fe', NULL, NULL, NULL, NULL, 0, 'Try this fast-paced game set in the western U.S. published by by White Wind, Inc.', '2', 'Beginner', 'Puffing Billy', NULL, '', NULL, NULL, 0, 20020714202833);
INSERT INTO event VALUES (74, 211, '1870', '1870', NULL, NULL, NULL, NULL, 0, 'Rail building and market manipulation in the trans-Mississippi. 1870 is published by Mayfair Games, Inc.', '2', 'Advanced', 'Puffing Billy', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (75, 212, '18xx Open', '18xx Open', NULL, NULL, NULL, NULL, 0, 'Choose your favorite 18xx variant and have at it. Possibilities include: 1829, 1835, 1853, 1856, 1869, 1870. Additional variants may be approved by the Puffing Billy Conductor. High scorers go on to play in the final.', '2', 'Advanced', 'Puffing Billy', NULL, NULL, 'Mayfair Games', NULL, 1, 20020714202833);
INSERT INTO event VALUES (76, 213, 'Streetcar / Line 1', 'Streetcar / Line 1', NULL, NULL, NULL, NULL, 0, 'The game of routes and racing with New Orleans streetcars. Published by Mayfair Games, Inc.', '2', 'Beginner', 'Puffing Billy', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (77, 214, 'Railroad Dynasty', 'Railroad Dynasty', NULL, NULL, NULL, NULL, 0, 'A new card based rail building game, great for new and old gamers alike.', '2', 'Beginner-Rules taught', 'Puffing Billy', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (78, 215, 'Silverton', 'Silverton', NULL, NULL, NULL, NULL, 0, 'Rail building in the mountains of Colorado.', '2', 'Intermediate', 'Puffing Billy', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (79, 216, 'Trainsport Series', 'Trainsport Series', NULL, 'Heather Barnhorst', NULL, NULL, 0, 'International Rail-Building Action!', '2', NULL, 'Puffing Billy', NULL, '', NULL, NULL, 0, 20020714202833);
INSERT INTO event VALUES (80, 301, 'The Vikings are Coming', 'The Vikings are Coming', NULL, 'Bill Daniel', NULL, NULL, 1, 'The time - 10th Century AD. The place - England. Life is good but it can\'t last. Wealth attracts trouble and the watchfires are lit. The Vikings are here. Can you save the Shire?', '2', 'Beginner', 'Miniatures', NULL, NULL, 'Colorado Military Historians', NULL, 1, 20020714202833);
INSERT INTO event VALUES (81, 302, 'Great Rail Wars: Bone Wars!', 'Great Rail Wars', 'Bone Wars!', 'Bill Daniel', NULL, NULL, 1, 'During the late 1880s, two great paleontologists, Cope & Marsh, are to scrounge the west for dinosaur bones. The race is on to find the greatest number of species. Both professors hate each other, and Marsh has just found a new bone bedland. Cope is in despair and may do anything to defeat his rival. ', '2', 'Beginner', 'Miniatures', NULL, NULL, 'Colorado Military Historians', NULL, 1, 20020714202833);
INSERT INTO event VALUES (82, 303, 'Battle Cattle: Operation Buttercup', 'Battle Cattle', 'Operation Buttercup', 'Sheila Davis', NULL, NULL, 1, 'Continuing destruction at the farm has wreaked havoc with Farmer Brown\'s finances.  Sadly, he\'s forced to sell beautiful Buttercup, the barnyard sweetheart.  Time for a Battle Cattle midnight rescue at the sale barn!', '2', 'Beginner', 'Miniatures', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (83, 304, 'Clan Wars: Battle at Tensen Village\r', 'Clan Wars', 'Battle at Tensen Village\r', 'Benn Williams and Aric Wieder', NULL, NULL, 1, 'Come play this Clan War demo put on by the Clan War demo team members Benn Williams and Aric Wieder.  Learn to play this fast-paced, exciting miniature game set in the L5R world.  Miniatures provided.\r', '2', 'Beginner-Rules Taught', 'Miniatures', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (84, 305, 'Clan Wars: Battle at the Kaiu Wall', 'Clan Wars', 'Battle at the Kaiu Wall', 'Benn Williams and Aric Wieder', NULL, NULL, 1, 'Experienced players will want to take part in a famous battle in which the Crab walls are besieged by attacking Shadowlands creatures.  Sponsored by the AEG Clan War demo team.  Bring a 1000 koku clan army.  \r', '2', 'Beginner', 'Miniatures', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (85, 306, 'Warhammer 40K', 'Warhammer 40K', NULL, 'Tom Brown', NULL, NULL, 1, 'Join us for this 3-round (maybe more) tournament. Bring your 1500-point army with NO special characters.  Use Codex if applicable. Standard Mission organization charts. For more information contact Tom Brown at DragonFire Games (303)543-9882', '2', 'Intermediate', 'Miniatures', NULL, NULL, 'DragonFire Games', NULL, 1, 20020714202833);
INSERT INTO event VALUES (86, 307, 'Blue Max', 'Blue Max', NULL, 'Ray Trochim & Shane Bower', NULL, NULL, 1, 'It\'s time to climb aboard your Sopwith camel and take to the skies. Fly over the European battlefield in your favorite WWI fighter planes. Dogfight with the enemy until only one side prevails. Bring your own 1/72 scale plane or use one that will be provided.', '2', 'Beginner', 'Miniatures', NULL, NULL, 'Colorado Military Historians', NULL, 1, 20020714202833);
INSERT INTO event VALUES (87, 308, 'Sword and the Flame: Roarke\'s Drift', 'Sword and the Flame', 'Roarke\'s Drift', 'Tony Fryer', NULL, NULL, 1, 'Ah, \'tis great to be British in South Africa. What did you say Smithers, Zulus attacking, why that is preposterous. Surely they know that we will mow them down with our guns? How many Zulus did you say? Oh dear, it seems that I\'ve gotten a spear thrown into my belly, that really stings. ', '2', 'Beginner', 'Miniatures', NULL, NULL, 'Colorado Military Historians', NULL, 1, 20020714202833);
INSERT INTO event VALUES (88, 309, 'General Quarters: Battle of Suva Bay', 'General Quarters', 'Battle of Suva Bay', 'Joel Tompkins', NULL, NULL, 1, 'Japanese and American cruisers battle it out in this historical battle of WW II that takes place in 1943.', '2', 'Beginner', 'Miniatures', NULL, NULL, 'Colorado Historical Gamers', NULL, 1, 20020714202833);
INSERT INTO event VALUES (89, 310, 'Napolean\'s Battles: Waterloo', 'Napolean\'s Battles', 'Waterloo', 'Steve Weeldreyer', NULL, NULL, 1, 'This is the fifth annual replay of this classic battle. Can the French finally win one?', '2', 'Intermediate', 'Miniatures', NULL, NULL, 'Colorado Historical Gamers', NULL, 1, 20020714202833);
INSERT INTO event VALUES (90, 311, 'Arkham Horror: Cthulhu Board Game in 3-D!', 'Arkham Horror', 'Cthulhu Board Game in 3-D!', 'Ed Peters & Matt Meadow', NULL, NULL, 1, 'Enjoy this BenCon favorite. Can you shut down all the gates before the Great Cthulhu himself appears?', '2', 'Beginner', 'Miniatures', NULL, NULL, 'Colorado Historical Gamers', NULL, 1, 20020714202833);
INSERT INTO event VALUES (91, 312, 'Age of Iron: Do It With The Lights On', 'Age of Iron', 'Do It With The Lights On', 'Steve Schultz', NULL, NULL, 1, 'Mississippi River, Sunrise, April 24, 1862.  Everything\'s gone wrong and the Union fleet hasn\'t moved.  Can Farragut run past Forts Jackson and St. Philip in the daylight?  Could the Confederate gunners have it any easier?  Give it a try in this American Civil War \'What If?\' scenario.', '2', 'Beginner', 'Miniatures', NULL, NULL, 'Colorado Military Historians', NULL, 1, 20020714202833);
INSERT INTO event VALUES (92, 313, 'Thundering Chariots: Chariot Racing in Ancient Rome', 'Thundering Chariots', 'Chariot Racing in Ancient Rome', 'Ron Lehman', NULL, NULL, 1, 'Can Juda Ben Hur win against Marcalla\'s scythed chariot? Come drive your chariot to victory. ', '2', 'Beginner', 'Miniatures', NULL, NULL, 'Colorado Historical Gamers', NULL, 1, 20020714202833);
INSERT INTO event VALUES (93, 314, 'Robo Rally: Robot Racing Fun', 'Robo Rally', 'Robot Racing Fun', 'Kevin Scarlet', NULL, NULL, 1, 'Can you touch all the flags before the others? This is the same as event 142. Board game passes will be accepted for this game.', '2', 'Beginner', 'Miniatures', NULL, NULL, 'Colorado Historical Gamers', NULL, 1, 20020714202833);
INSERT INTO event VALUES (94, 315, 'Drums along the Mohawk: French and Indian Wars', 'Drums along the Mohawk', 'French and Indian Wars', 'Greg Skelly', NULL, NULL, 1, 'Join in this exciting game of skirmish warfare during the French and Indian Wars. Can the British defeat the French? Or will the Indians be the key to a French victory?', '2', 'Beginner', 'Miniatures', NULL, NULL, 'Colorado Military Historians', NULL, 1, 20020714202833);
INSERT INTO event VALUES (95, 316, 'DBM: Alexander vs Porus', 'DBM', 'Alexander vs Porus', 'Doug Sundeth & Bill Faulkner', NULL, NULL, 1, 'Alexander and his Macedonian army face their toughest opponent in Porus, an Indian Rajah. Can Alexander\'s combined forces deal with the Indian Elephants and Chariots? Or will the massive bow power of the Indians be enough to withstand the Macedonian Phalanxes?', '2', 'Beginner', 'Miniatures', NULL, NULL, 'Colorado Military Historians', NULL, 1, 20020714202833);
INSERT INTO event VALUES (96, 317, 'Rally \'Round the Flag: Capture Those Guns', 'Rally \'Round the Flag', 'Capture Those Guns', 'Todd Pressley', NULL, NULL, 1, 'The Yankee batteries have been beating up the infantry pretty good.  Some fresh reinforcements provide an opportunity to move them out of their position and make the Yankees pay for their boldness.', '2', 'Beginner', 'Miniatures', NULL, NULL, 'Colorado Military Historians', NULL, 1, 20020714202833);
INSERT INTO event VALUES (97, 318, 'Spearhead: On the Road Again', 'Spearhead', 'On the Road Again', 'Todd Pressley', NULL, NULL, 1, 'The last Soviet armored column has just withdrawn.  The next objective is the outskirts of Stalingrad.  The scout aircraft have not reported any movement outside the city.  Can it really be yours for the taking?', '2', 'Beginner', 'Miniatures', NULL, NULL, 'Colorado Military Historians', NULL, 1, 20020714202833);
INSERT INTO event VALUES (98, 319, 'Western Front', 'Western Front', NULL, 'Dan Gururle', NULL, NULL, 1, 'It\'s time for the big push! Can the Germans achieve the breakthrough they need? Or will the allied machine guns and artillery mow them down? ', '2', 'Beginner', 'Miniatures', NULL, NULL, 'Colorado Historical Gamers', NULL, 1, 20020714202833);
INSERT INTO event VALUES (99, 320, 'Battlefleet Gothic Demo', 'Battlefleet Gothic Demo', NULL, 'Trey Moody', NULL, NULL, 1, 'In the far distant future of the 40th millennium, the galaxy is ravaged by warfare.  Vast armies of troops are deployed on a moment’s notice. Far above the battlefields of the future, massive fleets of starships maneuver and clash.  Prepare your ships and train your officers - the destiny of planets and civilizations rest in your hands!  With arsenals of torpedoes and weapons batteries, fleet commanders will be forced to think carefully about their actions or face the bitter cold of death in space.  So ignite the engines, raise your shields and plot a course for the stars!  Come try Games Workshop\'s newest game!  Beginners only, everything provided, and rules taught.  Hosted by GW Outrider Trey Moody and Games Workshop!', '0', 'Beginner-Rules Taught', 'Demonstration', NULL, NULL, 'Games Workshop', NULL, 1, 20020714202833);
INSERT INTO event VALUES (100, 321, 'Napoleon\'s Battles: Tournament', 'Napoleon\'s Battles', 'Tournament', 'Clay Bush', NULL, NULL, 1, 'A multi-round tournament. Bring your own 300-point, 1808 army or use one provided by the referee. DYO rule 10.3.3.1.1 limits special units, but may always include one minimum-size brigade of a restricted type. Scale - 15mm.', '2', 'Advanced', 'Miniatures', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (101, 322, 'Knight\'s  Cross', 'Knight\'s  Cross', NULL, 'Binhan Lin', NULL, NULL, 1, 'Come and learn this exciting new game of WW II arial combat, based upon the popular Blue Max system. Planes will be provided (feel free to bring your own 1/144th scale planes). There will be several rounds of flying, so stop by anytime during the event to jump in.', '2', 'Beginner-Rules Taught', 'Miniatures', NULL, NULL, 'Colorado Military Historians', NULL, 1, 20020714202833);
INSERT INTO event VALUES (102, 323, 'Warhammer 40K: Demonstration', 'Warhammer 40K', 'Demonstration', 'Tony Scott', NULL, NULL, 1, 'In the grim darkness of the far future, there is only war!  Join us to learn and play Games Workshops\' Warhammer 40,000!  Deep in space on the desert planet of Levilnor IV, the Dark Eldar have landed an assault team to destroy a critical base of operations.  Outnumbered 2 to 1, the fearless Space marines, must rescue the base and prevent it\'s destruction.  Five fun and furious sessions will be played.  Beginners level, with everything provided. All we are missing is YOU!\r\rHosted by Games Workshop Outrider Tony Scott.', '0', 'Beginner-Rules Taught', 'Miniatures', NULL, NULL, 'Games Workshop', NULL, 1, 20020714202833);
INSERT INTO event VALUES (103, 324, 'ShockForce: Demonstration', 'ShockForce', 'Demonstration', 'Chuck French', NULL, NULL, 1, 'Join Chuck as he teaches you the latest Science Fiction miniature game', '0', 'Beginner-Rules Taught', 'Miniatures', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (104, 325, 'Shockforce: Tournament', 'Shockforce', 'Tournament', 'Chuck French', NULL, NULL, 1, 'Bring 1500 points worth of troops and battle for the title \'Poobah of the Glasslands\'. Prizes to be announced. Help with conversions from other systems is avaliable. All army lists must be approved before they\'re used. Send your list to  DukeFnord@aol.com', '2', 'Beginner', 'Miniatures', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (105, 326, 'Car Wars: Mountain West Regionals', 'Car Wars', 'Mountain West Regionals', 'Mike Fal', NULL, NULL, 1, 'Join us in the Aspen Octagon for the regional championships of the 21st century\'s hottest sport, autoduelling!  This division 30 event will follow standard AADA tournament restrictions and rules that can be found at http://www.sjgames.com/car-wars/aada/rules.html. Custom car designs should be turned into the referee before the event if possible!  If you have any questions, please contact Mike Fal at mfal@rentaudio.com or (303)554-9686. ', '2', 'Beginner', 'Miniatures', NULL, NULL, 'Rocky Mountain Autoduelling Association', NULL, 1, 20020714202833);
INSERT INTO event VALUES (106, 327, 'Warhammer 40K: Tank Fist', 'Warhammer 40K', 'Tank Fist', 'David Ortiz', NULL, NULL, 1, 'Bring all your tanks!  No point limit! No characters or troops just support.\rAnything from support weapons to Titans. You may even bring in your own home made conversions, anything you have a datafax for.  Titans will use the full Inquistor rules.  All models must be fully represented, no substitutions.', '2', 'Beginner', 'Miniatures', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (107, 501, 'RPGA AD&D Benefit: Quest For Marvin', 'AD&D', 'Quest for Marvin', 'Tom Prusa', 'CHECKED', 'BE', 2, 'This is a great Tom Prusa scenario.', '0', 'Beginner', 'Role-Playing', NULL, '', 'the RPGA', 'QFMRV2AD', 1, 20020714203004);
INSERT INTO event VALUES (108, 502, 'RPGA Advanced Dungeons and Dragons Benefit: Don\'t Go There', 'Advanced Dungeons and Dragons', 'Don\'t Go There', 'Don Bingle and Saul Resnikoff', 'CHECKED', 'BE', 1, 'Whatever Eve\'s secret is, she\'s awfully touchy about it; not that there aren\'t a few things about your life that no one knows about.  In any case, everyone has to work together if an adventuring party is going to survive and, hopefully, be prosperous.  Times are tough and your party\'s last job was a disaster.  You\'ve been selling magic items just to eat.  Maybe this next town will have a job...a job better than mucking out stalls and baling hay for a meal.  Some danger, some excitement, some treasure...now that would be a good thing.', '3', 'Beginner', 'Role-Playing', NULL, NULL, 'the RPGA', NULL, 1, 20020714202833);
INSERT INTO event VALUES (109, 503, 'RPGA AD&D Benefit: Method to the Madness', 'AD&D', 'Method to the Madness', 'Pete and Kim Winz', 'CHECKED', 'BE', 1, 'You and the other inmates of the Black Island Institute are going on a trip! You can hardly wait to get back out into the real world again, but you\'re not quite sure how your odd companions will handle the experience. Come along and roleplay a very special character.', '3', 'Beginner', 'Role-Playing', NULL, NULL, 'the RPGA', 'MTMAD1AD', 1, 20020714202833);
INSERT INTO event VALUES (110, 504, 'RPGA AD&D: Information Please', 'AD&D', 'Information Please', 'Carla Hollar and Barbara Hysinger', 'CHECKED', 'MA', 1, 'A fine event.', '3', 'Intermediate', 'Role-Playing', NULL, '', 'the RPGA', NULL, 1, 20020714202833);
INSERT INTO event VALUES (111, 505, 'RPGA AD&D GrandMasters: Freudian Slips', 'AD&D', 'Freudian Slips', 'Tom Prusa', 'CHECKED', 'GM', 1, '\'I am Sigmund\'s Strength.  It is the best of all of Sigmund\'s attributes, the one that makes him succeed at his most important job of peasant hero.  No, I am Sigmund\'s Wisdom, the peacemaker, the driving force behind Sigmund.  No, I am Sigmund\'s...\'  Help Sigmund make it through the day, facing all the challenges together, so to speak.', '3', 'Advanced', 'Role-Playing', NULL, NULL, 'the RPGA', 'FRSLl1AD', 1, 20020714202833);
INSERT INTO event VALUES (112, 506, 'RPGA AD&D - Living City Benefit: Night of the Lunar Eclipse', 'AD&D - Living City', 'Night of the Lunar Eclipse', 'James Alan', 'CHECKED', 'BE', 1, 'Strange events have always occurred on nights such as these.  You have been roused on this dark night by the Temple of Selune.  You must find an ancient artifact before others with evil intent do.  Will you succeed, or will Raven\'s Bluff fall to the forces of evil?', '3', 'Beginner', 'Role-Playing', NULL, NULL, 'the RPGA', 'NTLUE1LC', 1, 20020714202833);
INSERT INTO event VALUES (113, 507, 'RPGA AD&D - Living City Benefit: Aethlaynmur\'s Plight', 'AD&D - Living City', 'Aethlaynmur\'s Plight', 'Jean Rabe', 'CHECKED', 'BE', 1, 'Aethlymur is in need of your assistance once again.  This time, though, he is not flying quite as high.  Help him to regain his celestial integrity, before all is lost.', '3', 'Beginner', 'Role-Playing', NULL, NULL, 'the RPGA', NULL, 1, 20020714202833);
INSERT INTO event VALUES (114, 508, 'RPGA AD&D - Living City: Crawl II: Another Temple, Another Time', 'AD&D - Living City', 'Crawl II: Another Temple, Another Time', 'Kevin Polster', 'CHECKED', NULL, 1, 'Yes, it\'s time again to go through another ruin, to discover what riches possibly lie within.   Of course, there\'s always the possibility that this is nothing more than what it is, a ruin.', '3', 'Beginner', 'Role-Playing', NULL, NULL, 'the RPGA', NULL, 1, 20020714202833);
INSERT INTO event VALUES (115, 509, 'RPGA AD&D - Living City Interactive Benefit: The Night Chicago', 'AD&D - Living City Interactive', 'The Night Chicago Died', 'Tim Creese', 'CHECKED', 'BE', 1, 'Have you ever dreamed of having a rich uncle who left you a fortune? I\r\nknow I do - especially when the work day\'s grueling...   Come pay your last respect to Chicago and perhaps garner some respect for yourself as well...', '3', 'Beginner', 'Role-Playing', NULL, '', 'the RPGA', NULL, 0, 20020714202833);
INSERT INTO event VALUES (116, 510, 'RPGA AD&D - Living Jungle Benefit: Terror From Below', 'AD&D - Living Jungle', 'Terror From Below', 'Leonard Dessert', 'CHECKED', 'BE', 1, 'You have been sent on a mission that you cannot tell anyone about even when you are done with it.  It seems like the world is coming apart at the seams, and you thought you were just attending a birthday party.  Good luck.', '3', 'Beginner', 'Role-Playing', NULL, NULL, 'the RPGA', NULL, 1, 20020714202833);
INSERT INTO event VALUES (117, 511, 'RPGA AD&D - Living Death Jorvik', 'AD&D - Living Death', 'Jorvik', 'Robert Tomihiro', 'CHECKED', 'BE', 1, 'The town of Camden, South Carolina was a quiet town.  Now it is a different case.  What could be causing the problems?  That is for you to find out, as well as live to tell the tale.', '3', 'Beginner', 'Role-Playing', NULL, '', 'the RPGA', NULL, 1, 20020714202833);
INSERT INTO event VALUES (118, 512, 'RPGA AD&D - Living Procampur: Deglath\'s Lament', 'AD&D - Living Procampur', 'Deglath\'s Lament', NULL, 'CHECKED', NULL, 1, 'The Lord Grimm requires your help once again.  A murder has been committed and he is the prime suspect!  Will you be able to find the necessary information in time to save him from the gallows?', '3', 'Beginner', 'Role-Playing', NULL, NULL, 'the RPGA', NULL, 1, 20020714202833);
INSERT INTO event VALUES (119, 513, 'RPGA Call of Cthulhu: Head of the Master', 'Call of Cthulhu', 'Head of the Master', 'Gary and Kimberly Labrecque', 'CHECKED', NULL, 1, 'The Head of the Master has been stolen from the Miskatonic University!!  Professor Bradley has requested you to help him. Find the artifact and who stole it before....well, who knows what could happen.', '3', 'Beginner', 'Role-Playing', NULL, NULL, 'the RPGA', 'CT', 1, 20020714202833);
INSERT INTO event VALUES (120, 514, 'RPGA Dragonlance 5th Age: Nightsinger\'s Flight', 'Dragonlance 5th Age', 'Nightsinger\'s Flight', 'Jacqulyne Cassada, Anne Rea, Carla Holla', 'CHECKED', NULL, 1, 'The characters travel to the forbidden, beleaguered land of Silvanesti to find an artifact from the Age of Dreams, but have they reached the right place?  Fourth in the Visions of Destiny campaign series.', '3', 'Beginner', 'Role-Playing', NULL, NULL, 'the RPGA', 'D5', 1, 20020714202833);
INSERT INTO event VALUES (121, 515, 'RPGA Star Wars Benefit: Trilogy-3', 'Star Wars', 'Trilogy-3', 'Jean Rabe', 'CHECKED', 'BE', 1, NULL, '3', 'Beginner', 'Role-Playing', NULL, NULL, 'the RPGA', NULL, 1, 20020714202833);
INSERT INTO event VALUES (122, 516, 'RPGA Paranoia: Nothing Complex', 'Paranoia', 'Nothing Complex', 'Don Bingle', 'CHECKED', NULL, 1, 'Life was wonderful until you became a Troubleshooter.  Surely your ensemble can convince the Wizard of Programming to renew your Tri-Dee show.', '3', 'Beginner', 'Role-Playing', NULL, NULL, 'the RPGA', NULL, 1, 20020714202833);
INSERT INTO event VALUES (123, 517, 'Deadlands: Bar Brawl ', 'Deadlands', 'Bar Brawl', 'Aric Wieder', NULL, NULL, 1, 'Come learn the ways of the Wierd West in this action packed, rough and tough event in the town of Rolling Rock.  During the towns first ever Fists of Steel competition, something goes terribly wrong, and its up to the heroes to find out before the fights start.  This is a beginner adventure.', '3', 'Beginner-Rules Taught', 'Role-Playing', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (124, 518, 'Deadlands: Valley Run', 'Deadlands', 'Valley Run', 'Aric Wieder', NULL, NULL, 1, 'When the good folks of Red Valley need supplies for a war with a local road gang the Heroes are asked to help.  All they need to do is deliver the goods through hostile territory, and return the payment to the main store.  Sound simple enough for ya? Come test your skills.  Please make characters prior to the event.  I will have a meeting prior to the event to go over characters.', '3', 'Intermediate', 'Role-Playing', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (125, 519, 'Providence: A New Beginning', 'Providence', 'A New Beginning', 'Aric Wieder', NULL, NULL, 1, 'Welcome to Providence. Here you can learn to be a hero again in this\r\nintroductory adventure. There has been a recent sighting of one of the\r\nGreat Gates in the jungles outside of a deserted city. You will have to\rsolve the mystery of the forgotten city to find out if the rumor is\r\ntrue. Characters will be provided and rules will be taught.\r\nLimit of 6 players.\r\n', '3', 'Beginner', 'Role-Playing', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (126, 520, 'Legend of the Five Rings: The Curse of Myoto Ieyasu', 'Legend of the Five Rings', 'The Curse of Myoto Ieyasu', 'Chris Weeda', NULL, NULL, 1, 'Myoto Ieyasu is an unseemly merchant whose wares have always been mediocre at best.  However, the events that happen after he goes through town are unusual and they\'re getting worse with each town.  Your band of Kuni Witch Hunters must find out the cause and stop it before he reaches his next destination.', '3', 'Beginner-Rules Taught', 'Role-Playing', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (127, 521, 'Legend of the Five Rings: The Art of Intrigue', 'Legend of the Five Rings', 'The Art of Intrigue', 'Chris Weeda', NULL, NULL, 1, 'The Scorpion Clan is the best at finding out everyone\'s dirty little secrets.  Of course, they also have their own habits as well.  Will you be able to find out their vices before they put you in one of their own?', '3', 'Beginner', 'Role-Playing', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (128, 522, 'Universe: Always Faithful', 'Universe', 'Always Faithful', 'Michael Wilkinson', NULL, NULL, 1, 'You\'re a soldier attached to the Supremacy National Army\'s \r\nPowered Assault Armor Division.  It is your responsibility to\r\nrecapture the Harrana Foothills.  Beginner-Rules Taught.\r\n', '3', 'Beginner-Rules Taught', 'Role-Playing', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (129, 523, 'Universe: What\'s that Green Stuff?!!', 'Universe', 'What\'s that Green Stuff?!!', 'Michael Wilkinson', NULL, NULL, 1, 'You\'re part of a starship crew that makes a long run to a very \r\nremote government installation to deliver supplies and pick \r\nup a mysterious cargo.  The job is tedious, but the pay is great.\r\nBeginner-Rules Taught.\r\n', '3', 'Beginner-Rules Taught', 'Role-Playing', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (130, 524, 'Universe: Piece of Mind\r', 'Universe', 'Piece of Mind\r', 'Markus Wilkinson', NULL, NULL, 1, 'Living on the edge of civilization always appealed to you, so you \r\nsigned up with a construction company to help build a remote \r\ncolony.  When you arrive, you\'re met by angry colonists bearing \r\ntorches and pichforks.  Don\'t you love life on the frontier?  \r\nBeginner-Rules Taught.\r\n', '3', 'Beginner-Rules Taught', 'Role-Playing', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (131, 525, 'Call of Cthulhu: You too can Cthulhu', 'Call of Cthulhu', 'You too can Cthulhu', 'Jefferey M. Carey', NULL, NULL, 1, 'This event is primarily designed fo rhtose who have never palyed Call of Cthulu, but want to learn!  A friend has called for your help.  Can you arrive to aid him in time and solve an ancient mystery in the process?  Players will be allowed to partially make their own characters.', '3', 'Beginner', 'Role-Playing', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (132, 526, 'Call of Cthulhu: Grace Under Pressure', 'Call of Cthulhu', 'Grace Under Pressure', 'John Tynes', NULL, NULL, 1, 'As a member of a deep-sea expedition team you are sent to discover new mysteries.  But nothing could prepare you for the unfathomable horror that reaches out from below.  This definitely was not in the job description!', '3', 'Beginner', 'Role-Playing', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (133, 527, 'Cyberpunk 2020: 1st Annual Desert Rat Extravaganza', 'Cyberpunk 2020', '1st Annual Desert Rat Extravaganza', 'Dave Grant and Martin Linck', NULL, NULL, 1, 'Your local fixer has hired you out for an exciting assignment full of blood, mayhem, and prizes, prizes, prizes!!!  Battle it out with other players in this Loki\'s Wine style free-for-all.  Prizes include a brand new BMW Aerodyne, an all expenses paid trip to the Pacific Rim, and maybe even your life!  Players will be asked to submit their own characters as per the rules available at Con HQ.  A revised edition of Friday Night Firefight will also be available.  There will be real prizes available for the winners.  If you have any questions, please call the judge at 303-758-2920', '3', 'Beginner', 'Role-Playing', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (134, 528, 'Earthdawn: The Release of Pain', 'Earthdawn', 'The Release of Pain', 'Drew Caldwell', NULL, NULL, 1, 'A group of heroes is sent to help a small girl in distress.  What starts out as a simple mission, though, quickly becomes significantly more complicated.  Will you be able to decipher the mystery and bring peace where once there was only pain?', '3', 'Beginner', 'Role-Playing', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (135, 529, 'Toon - Masters of Toon: Masters of Toon - Toonathon II', 'Toon - Masters of Toon', 'Masters of Toon - Toonathon II', 'Sumit Sarkar', NULL, NULL, 1, 'Kung Pow Chicken, Bruise Li, and the regular cast of characters are back to foil the bad guys using mad martial arts skills and bad foreign accents. ', '3', 'Beginner-Rules Taught', 'Role-Playing', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (136, 530, 'Toon - Atomic Monster Theatre: Toonathon II', 'Toon - Atomic Monster Theatre', 'Toonathon II', 'Tim Orel', NULL, NULL, 1, 'Ever wanted to star in your own low-budget horror movie?  With the rash of monster movies making a comeback, here\'s our attempt at more thrills, chills and mayhem than any Grade Z picture ever.  Come see how it\'s done Toon-style.', '3', 'Beginner-Rules Taught', 'Role-Playing', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (137, 531, 'Toon - Cybertoon 2020 1/2: Toonathon II', 'Toon - Cybertoon 2020 1/2', 'Toonathon II', 'Dave Grant', NULL, NULL, 1, 'In the near-future, fun is illegal.  It\'s up to you and your band of misfits to spread laughter and mischief in this dangerous and exciting time with high-tech gadgets, computer gizmos, and your limitless imagination.', '3', 'Beginner-Rules Taught', 'Role-Playing', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (138, 532, 'Toon - Dungeons and Toons: Toonathon II', 'Toon - Dungeons and Toons', 'Toonathon II', 'Jay Hafner', NULL, NULL, 1, 'Experience a world of TRUE fantasy in this parody of another not-quite-as-popular game.  Journey through a distant land, a world of blunt swords, backfiring spells, and monsters with more bark than bite.', '3', 'Beginner-Rules Taught', 'Role-Playing', NULL, NULL, NULL, NULL, 1, 20020714202833);
INSERT INTO event VALUES (139, 533, 'World of Darkness', 'World of Darkness', NULL, NULL, NULL, NULL, 1, 'Recently there has been strange happenings within the city of Denver.  Strange rumors of vampire raids, and firebombs.  Swedish medical center destroyed, rock bottom brewery burned to the ground, and a house vaparized by \'demon-spawn.\'  Now you\'ve been hired to find a mising person and all the information you\'ve been given is that this person is the key to unlock what\'s been going on.\r', '3', 'Beginner-Rules Taught', 'Role-Playing', NULL, NULL, NULL, NULL, 1, 20020714202833);
